package com.hq.payms.common.utils;

import javax.crypto.Cipher;
import javax.crypto.spec.SecretKeySpec;

/**
 * AES算法加密解密工具类
 * @author: wujunwei 
 * @version: v1.0  
 * @since: JDK 1.8
 */
public class AESUtil {  
  
    /** 
     * 密钥算法 
     */  
    private static final String ALGORITHM = "AES";  
    
    /** 
     * 加解密算法/工作模式/填充方式 
     */  
    private static final String ALGORITHM_MODE_PADDING = "AES/ECB/PKCS7Padding";  
  
    /** 
     * AES加密 
     *  
     * @param data 
     * @return 
     * @throws Exception 
     */  
    public static String encryptData(String data, String key) throws Exception {  
        // 创建密码器  
        Cipher cipher = Cipher.getInstance(ALGORITHM_MODE_PADDING);  
        // 初始化  
        SecretKeySpec secretKey = new SecretKeySpec(MD5Util.MD5Encode(key, "UTF-8").toLowerCase().getBytes(), ALGORITHM); 
        cipher.init(Cipher.ENCRYPT_MODE, secretKey);  
        return Base64Util.encode(cipher.doFinal(data.getBytes()));  
    }  
  
    /** 
     * AES解密 
     *  
     * @param base64Data 
     * @return 
     * @throws Exception 
     */  
    public static String decryptData(String base64Data, String key) throws Exception {  
        // 创建密码器  
        Cipher cipher = Cipher.getInstance(ALGORITHM_MODE_PADDING);  
        // 初始化
        SecretKeySpec secretKey = new SecretKeySpec(MD5Util.MD5Encode(key, "UTF-8").toLowerCase().getBytes(), ALGORITHM);
        cipher.init(Cipher.DECRYPT_MODE, secretKey);  
        return new String(cipher.doFinal(Base64Util.decode(base64Data)));  
    }  
  
    public static void main(String[] args) throws Exception {  
        //String A 为测试字符串，是微信返回过来的退款通知字符串  
        String A = "";
        String key = "test key";//此处为测试key，正式环境要替换成商户密钥 
        String B = AESUtil.decryptData(A, key);  
        System.out.println(B);  
    }  
  
}  